#include <Arduino.h>

String HTML = "<!DOCTYPE html><html> <head><meta charset=UTF-8><meta name=viewport content='width=device-width, initial-scale=1.0'><title>Open WiFi Project</title><link rel=stylesheet type=text/css href=style.css></head> <body> <div id=app> <header> <h1>Open Wifi Project</h1> <p>'The internet is a human right'</p> </header> <main> <form class=card> <h2>Login</h2> <input type=text placeholder=username> <input type=password placeholder=password> <button id=login-btn>login</button> <button id=signup-btn>sign up</button> <div id=signup-message class=message> <p>Not registered?</p> <a id=signup-link href=#>Signup for an account</a> </div> <div id=login-message class=message> <a id=login-link href=#>Login</a> </div> </form> </main> <footer class=card> <p> Our internet connection is provided for free by generous donors like you </p> <a id=support-link href=#>Consider Supporting</a> </footer> </div> <script src=script.js defer></script> </body> </html> <style>body{font-family:sans-serif;-webkit-font-smoothing:antialiased;-moz-osx-font-smoothing:grayscale;margin:0;color:#fff}#app{background:linear-gradient(to bottom,#2c3e50,#3498db);height:100vh;display:grid;grid-template-columns:1fr;grid-template-rows:auto 1fr auto;grid-template-areas:'header' 'main' 'footer'}#app>header{grid-area:header}#app>main{grid-area:main;display:flex;align-items:center;justify-content:center;overflow:auto}#app>footer{grid-area:footer}header{display:flex;flex-direction:column;justify-content:center;align-items:center;height:155px}header>h1{font-weight:bold;text-align:center;font-size:2.4rem;margin:0}header>p{color:#b9bbd3;font-size:.8rem;text-align:center;font-style:italic;padding:0}.card{background:#fff;padding:.8rem;margin:1rem;max-height:300px;box-shadow:0 4px 8px 0 rgba(0,0,0,0.2),0 6px 20px 0 rgba(0,0,0,0.19)}form>h2{font-size:3rem;margin:0 0 1rem 0;color:black}form input,form button{outline:0;width:100%;margin-bottom:15px;padding:15px;box-sizing:border-box;font-size:14px}form input{background:#f0f0f0;border:0}form input:focus{border:1px solid #b6b6b6}form button{text-transform:uppercase;background:#3498db;border:0;color:#fff;transition:all .3s ease;cursor:pointer}form button:hover,form button:active,form button:focus{background:#1b80c3}#login-btn{display:block}#signup-btn{display:none}.message{margin-top:.7rem;font-size:1rem;color:#000}.message a{color:#006fb9}a:hover{color:#4fb9ff}#signup-message{display:flex;justify-content:space-evenly}#login-message{display:none;justify-content:flex-end;align-items:center}form p{padding:0 .3rem 0 0;margin:0}footer{display:flex;flex-direction:column;align-items:center;justify-content:center;height:60px}footer p{padding:.4rem;margin:0;font-size:1rem;font-weight:bold;text-align:center;color:#000}footer a{color:#006fb9;font-weight:bold}</style><script>const signupBtn=document.getElementById('signup-btn');const signupMessage=document.getElementById('signup-message');const signupLink=document.getElementById('signup-link');const loginBtn=document.getElementById('login-btn');const loginMessage=document.getElementById('login-message');const loginLink=document.getElementById('login-link');signupLink.addEventListener('click',()=>{loginBtn.style.display='none';signupMessage.style.display='none';signupBtn.style.display='flex';loginMessage.style.display='flex'});loginBtn.addEventListener('click',()=>{loginBtn.style.display='flex';signupMessage.style.display='flex';signupBtn.style.display='none';loginMessage.style.display='none'});</script> <style></style><script></script>";

String html()
{
  return HTML;
}

